function padZero(num) {
    return (num < 10 ? "0" : "") + num;
}

function setClick(){
    var data = JSON.parse(document.getElementById("data").textContent);
    var temp = document.getElementById('temp').checked;
    var outtemp = document.getElementById('outtemp').checked;
    var jsonData = document.getElementById('jsonData');
    console.log(data);
    console.log(temp);
    console.log(outtemp);
    var Data = new Array();
    for(var i = 0; i < data.length; i++){
        var d = new Date(data[i].timestamp);
        var time = d;
        console.log(time);
        Data.push({
            "date": time,
            "temp": data[i].temp,
            "outtemp": data[i].outtemp,

        });

    }

    am4core.ready(function() {

        am4core.useTheme(am4themes_animated);
        // Themes end

        var chart = am4core.create("chartdiv", am4charts.XYChart);

        chart.data = Data.reverse();
        var dateAxis = chart.xAxes.push(new am4charts.DateAxis());
        dateAxis.groupData = false;
        dateAxis.renderer.grid.template.location = 0.5;
        dateAxis.renderer.labels.template.location = 0.5;
        dateAxis.renderer.minGridDistance = 30;
        dateAxis.startLocation = 0.5;
        dateAxis.endLocation = 0.5;
        dateAxis.renderer.labels.template.rotation = 45;
        dateAxis.dateFormats.setKey("second", "[font-size: 12px]HH:mm:ss");
        dateAxis.periodChangeDateFormats.setKey("second", "[bold]HH:mm:ss");
        dateAxis.periodChangeDateFormats.setKey("minute", "[bold]HH:mm:ss");
        dateAxis.periodChangeDateFormats.setKey("hour", "[bold]yyyy/MM/dd");
        dateAxis.periodChangeDateFormats.setKey("day", "[bold]yyyy/MM/dd");

        console.log("dateAxis fin");
        // グリッド間隔
        var valueAxis = chart.yAxes.push(new am4charts.ValueAxis());
        valueAxis.title.text = "temperature (℉)";

        // スクロールバーに気温のグラフを表示
        var scrollbarX = new am4core.Scrollbar();        
        var scrollbarY = new am4core.Scrollbar(); 


        // Create series

        if(temp){
            // 時系列データ
            var series1 = chart.series.push(new am4charts.LineSeries());
            series1.name = "temp";
            series1.dataFields.valueY = "temp";
            series1.dataFields.dateX = "date";
            series1.tooltipText = "{dateX.formatDate('yyyy/MM/dd HH:mm:ss')} \ntemperature: {valueY} ℉[/]"; 
            var bullet = series1.bullets.push(new am4charts.CircleBullet());
              bullet.circle.stroke = am4core.color("#fff");
              bullet.circle.strokeWidth = 2;

            series1.tooltip.pointerOrientation = "vertical";
            series1.strokeWidth = 2;
            // series1.tensionX = 0.7;
            if(!outtemp) jsonData.src="https://meijoryoga.mybluemix.net/temp";
        }

        if(outtemp){
            // Create series
            var series2 = chart.series.push(new am4charts.LineSeries());
            series2.name = "outtemp";
            series2.dataFields.valueY = "outtemp";
            series2.dataFields.dateX = "date";
            series2.tooltipText = "{dateX.formatDate('yyyy/MM/dd HH:mm:ss')} \ntemperature: {valueY} ℉[/]"; 

            var bullet = series2.bullets.push(new am4charts.CircleBullet());
             bullet.circle.stroke = am4core.color("#fff");
             bullet.circle.strokeWidth = 2;

            series2.tooltip.pointerOrientation = "vertical";
            series2.strokeWidth = 2;
            if(!temp) jsonData.src="https://meijoryoga.mybluemix.net/outtemp";
        }
        if(temp && outtemp){
            jsonData.src="https://meijoryoga.mybluemix.net/Data";
        }
        if(!temp && !outtemp){
            jsonData.src="https://meijoryoga.mybluemix.net/noData";
        }
        chart.legend = new am4charts.Legend();
        console.log("llll");
        chart.cursor = new am4charts.XYCursor();
        chart.cursor.maxTooltipDistance = 20;

        chart.scrollbarX = scrollbarX;
        chart.scrollbarY = scrollbarY;


    }); // end am4core.ready()
}

